--新建商品类别表（Category）
create table Category(
Cid int primary key identity(1,1),
cname nvarchar(10)  not null unique,
Description nvarchar(10) null)--新建商品信息表(Ware)create table Ware(Wid int primary key identity(1,1),Wname nvarchar(10)  not null unique,Cid int not null foreign key references Category(Cid),PurchasePrice decimal(10,2)not null check(len(PurchasePrice)>0),SalesPrice decimal(10,2)not null check(len(SalesPrice)>0),Amount int check(len(Amount)>0))--新建员工表（Employee）create table Employee(Eid int primary key identity(1001,1), Ename nvarchar(5)not null,Epassword varchar(10)check(len(Epassword)>6),Remark nvarchar (20)null)--新建商品销售记录表（SalesInfo）create table SalesInfo(Sid int primary key identity(1,1),Wid int not null foreign key references Ware(Wid ),SalesDate datetime not null,SalesAmount int not null check(len(SalesAmount)>0),Eid int not null foreign key references Employee(Eid))